package main

import "fmt"

func main() {
	string := ""
	fmt.Println("Marker is at index", find_marker(string, 4))
	fmt.Println("Start of message marker is at index", find_marker(string, 14))
}

func find_marker(packet string, offset int) int {
	chars := make([]rune, 0)
	for marker, char := range packet {
		if len(chars) > offset && isUnique(chars[len(chars) - offset:]) {
			return marker
		}
		chars = append(chars, char)
	}
	return -1
}

func isUnique(chars []rune) bool {
	uniqueArray := make(map[rune]bool)
	for _, char := range chars {
		uniqueArray[char] = true
	}
	return len(uniqueArray) == len(chars)
}
